        if (bal < currentAmount) {
            uint256 balWithdraw = currentAmount.sub(bal);
            IReactFarm(farm).leaveStaking(balWithdraw);
            uint256 balAfter = available();
            uint256 diff = balAfter.sub(bal);
            if (diff < balWithdraw) {
                currentAmount = bal.add(diff);
            }
